package JavaBasics;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Primitive Data types:int,double,char,boolean
		
		//integer
		int i =20;
		i=30;
		int j= -2;
		int p = 0;
		System.out.println(j); //prints in console
		System.out.println(i+j);
		
		//Double:
		double d = 12.66;
		double b= -34.55;
		System.out.println(b);//short cut:syso press ctrl space
		double c = 100;  // it indicates 100.00
		
		
	//char:only single digit value,should be within single quotes	
		char c1 = 'a';
		char c2 = '1';  //in single quotes so number is accepted
		char c3 = 'a';
		System.out.println(c3);
		
	//Boolean
		boolean b1 =true;
		boolean b2 = false;
		
	//String is a class, but we can use it for DataType
		String s = "hi";
		String s2 = "Hello";
		String s3 = "1000"; //in double quotes 

	}

}
